import React from "react";
import { useDispatch } from "react-redux";
import { deleteTaskAction } from "../../redux/actions/actions";

const Task = (props) =>{
    const {taskName, id} = props.task;
    const dispatch = useDispatch();
    const deleteTask = () => {
        dispatch(deleteTaskAction(id))
    }
    const randomColor = "#" + Math.floor(Math.random()*16777215).toString(16);

    // Inline style object with the random background color
    const rowStyle = {
        backgroundColor: randomColor
    };
    return(
        <div className="task-row" style={rowStyle}>
            <span>{taskName}</span>
            <img src="../assets/delete.png" onClick={deleteTask}/>
        </div>


    )
}

export default Task
