import './App.css';
import AddForm from './Components/AddForm/addForm';
import Task from './Components/TaskRow/task';
import { useSelector } from 'react-redux';

function App() {
  const tasks = useSelector(state=>state.tasks.tasks)
  return (
    <div>
      <AddForm />
      {tasks.map((a)=>{
        return <Task task = {a} />
      })}
    </div>

  );
}

export default App;
